import axios, { Axios } from 'axios';
import React, { useContext, useEffect, useReducer } from 'react'
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import { PayPalButtons, usePayPalScriptReducer } from '@paypal/react-paypal-js';
import ListGroup from 'react-bootstrap/ListGroup';
import Card from 'react-bootstrap/Card';
import { Helmet } from 'react-helmet-async';
import { Navigate, useNavigate, useParams, Link } from 'react-router-dom';
import LoadingSign from '../components/LoadingSign'
import MessageBox from '../components/MessageBox'
import { Store } from '../Store';
import { getError } from '../utils';

const reducer = (state,action) => {
    switch(action.type) {
      case 'FETCH_REQUEST': 
        return {...state, loading: true};
      case 'FETCH_SUCCESS':
        return{...state, error: '',order: action.payload, loading: false};
      case 'FETCH_FAIL':
        return{...state, loading: false, error: action.payload};
      default:
        return state;
    }
  }

export default function OrderScreen() {
    const {state} = useContext(Store);
    const {userInfo} = state;
    const navigate = useNavigate();
    const params = useParams();
    const {id: orderId} = params;

    const [{loading, error, order}, dispatch]= useReducer(reducer, {
        loading: true,
        order: {},
        error: '',
    })

    const[{isPending}, paypalDispatch] = usePayPalScriptReducer();

    function createOrder(data, actions) {
        return actions.order
        .create({
            purchase_units: [
                {
                    amount: {value: order.totalPrice},
                },
            ],
        })
        .then((orderID) => {
            return orderID;
        })
    }

    useEffect(() => {
        const fetchOrder = async () => {
            try {
                dispatch({type: 'FETCH_REQUEST'});
                const {data} = await axios.get(`/api/orders/${orderId}`, {
                    headers: {authorization: `Bearer ${userInfo.token}`},
                });
                dispatch({type: 'FETCH_SUCCESS', payload: data});
            }   catch(err) {
                dispatch({type: 'FETCH_FAIL', payload: getError(err)});                
            }
        }
        if(!userInfo) {
            return navigate('/login')
        }
        if(!order._id || (order._id && order._id !== orderId)){
            fetchOrder()
        } else{
            const loadPaypalScript = async () => {
                const {data: clientId} = await Axios.get('/api/keys/paypal', {
                    headers: { authorization: `Bearer ${userInfo.token}`},
                });
                paypalDispatch({
                    type: 'resetOptions',
                    value: {
                        'client-id': clientId,
                        currency: 'USD',
                    },
                })
                paypalDispatch({type: 'setLoadingStatus', value: 'pending'});
            }
            loadPaypalScript();
        }
    },[order, userInfo, navigate, paypalDispatch,orderId]);

    return loading ? (

    <LoadingSign></LoadingSign> ) :
    error ? (
        <MessageBox variant='danger'>{error}</MessageBox>
    ) : (
        <div>
            <Helmet>
                <title>Order Review</title>
            </Helmet>
            <h1 className='my-3'>Order Review</h1>
            <Row>
                <Col md={8}>
                    <Card className='mb-3'>
                        <Card.Body>
                            <Card.Title>Shipping</Card.Title>
                            <Card.Text>
                                <strong>Name:</strong> {order.shippingAddress.fullName} <br/>
                                <strong>Address: </strong> {order.shippingAddress.address},
                                {order.shippingAddress.city}, {order.shippingAddress.zipCode}, 
                                {order.shippingAddress.country}
                            </Card.Text>
                            {order.isDelivered ? (
                                <MessageBox variant='success'>
                                    Delivered at {order.deliveredAt}
                                </MessageBox>
                            ) : (
                                <MessageBox variant='danger'>Not Delivered</MessageBox>
                            )}
                            <Link to='/payment'>Edit</Link>
                        </Card.Body>
                    </Card>
                    <Card className='mb-3'>
                        <Card.Body>
                            <Card.Title>Payment</Card.Title>
                            <Card.Text>
                                <strong>Method:</strong> {order.paymentMethod}
                            </Card.Text>
                            {order.isPaid ? (
                                <MessageBox variant='success'>
                                    Paid at {order.paidAt}
                                </MessageBox>
                            ) : (
                                <MessageBox variant='danger'>Not Paid</MessageBox>
                            )}
                            <Link to='/payment'>Edit</Link>
                        </Card.Body>
                    </Card>
                    <Card className='mb-3'>
                    <Card.Body>
                        <Card.Title>Items</Card.Title>
                        <ListGroup variant='flush'>
                            {order.orderItems.map((item) => (
                                <ListGroup.Item key={item.id}>
                                    <Row className='align-items-center'>
                                        <Col md={6}>
                                            <img 
                                            src={item.image}
                                            alt='qwer'
                                            className='img-fluid rounded img-thumbnail'>
                                            </img>{' '}
                                            <Link to={`/product/${item.slug}`}>{item.name}</Link>
                                        </Col>
                                        <Col md={3}>
                                            <span>{item.quantity}</span>
                                        </Col>
                                        <Col md={3}>${item.price}</Col>                                            
                                    </Row>
                                </ListGroup.Item>
                            ))}
                        </ListGroup>
                        <Link to='/cart'>Edit</Link>
                    </Card.Body>
                </Card>
                </Col>
                <Col md={4}>
                    <Card className='mb-3'>
                        <Card.Body>
                            <Card.Title>Order Summary</Card.Title>
                            <ListGroup variant='flush'>
                                <ListGroup.Item>
                                    <Row>
                                        <Col>Items</Col>
                                        <Col>${order.itemsPrice.toFixed(2)}</Col>
                                    </Row>
                                </ListGroup.Item>
                                <ListGroup.Item>
                                    <Row>
                                        <Col>Shipping</Col>
                                        <Col>${order.shippingPrice.toFixed(2)}</Col>
                                    </Row>
                                </ListGroup.Item>
                                <ListGroup.Item>
                                    <Row>
                                        <Col><strong>Tax</strong></Col>
                                        <Col><strong>${order.taxPrice.toFixed(2)}</strong></Col>
                                    </Row>
                                </ListGroup.Item>
                                <ListGroup.Item>
                                    <Row>
                                        <Col>Total</Col>
                                        <Col>${order.totalPrice.toFixed(2)}</Col>
                                    </Row>
                                </ListGroup.Item>
                                {!order.isPaid && (
                                    <ListGroup.Item>
                                        {isPending ? (
                                            <LoadingSign />
                                        ) :
                                        (<div>
                                            <PayPalButtons 
                                            createOrder={createOrder} 
                                            onApprove={onApprove} 
                                            onError={onError}></PayPalButtons>
                                        </div>)
                                        }
                                    </ListGroup.Item>
                                )}
                            </ListGroup>
                        </Card.Body>
                    </Card>
                </Col>
            </Row>
        </div>
    )
  
}
