import React, { useContext, useEffect, useReducer } from 'react'
import { Helmet } from 'react-helmet-async'
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import CheckoutSteps from '../components/CheckoutSteps';
import {Store} from '../Store';
import { getError } from '../utils';
import { toast } from 'react-toastify';
import {useNavigate, Link} from 'react-router-dom'
import ListGroup from 'react-bootstrap/ListGroup';
import  Axios  from 'axios';
import LoadingSign from '../components/LoadingSign';

const reducer = (state, action) => {
    switch(action.type) {
        case 'CREATE_REQUEST':
            return {...state, loading: true}
        case 'CREATE_SUCCESS':
            return {...state, loading: false}
        case 'CREATE_FAIL':
            return {...state, loading: false}
        default:
            return state;    
    }
}

export default function PlaceOrderScreen() {
    const {state, dispatch: ctxDispatch} = useContext(Store);
    const {cart, userInfo} = state;
    const navigate = useNavigate();

    const [{loading}, dispatch] = useReducer(reducer, {
        loading: false,
    });

    const round2 = (num) => Math.round(num * 100 + Number.EPSILON) / 100;
    cart.itemsPrice = round2(
        cart.cartItems.reduce((a,c) => a + c.quantity * c.price, 0)
    );
    cart.shippingPrice= cart.itemsPrice > 100 ? round2(0) : round2(10);
    cart.taxPrice = round2(0.15 * cart.itemsPrice);
    cart.totalPrice = cart.taxPrice + cart.shippingPrice + cart.itemsPrice


    const placeOrderHandler = async() =>{ 
        try {
           dispatch({type: 'CREATE_REQUEST'});
           
           const {data} = await Axios.post(
            '/api/orders',
            {
                orderItems: cart.cartItems,
                shippingAddress: cart.shippingAddress,
                paymentMethod: cart.paymentMethod,
                itemsPrice: cart.itemsPrice,
                shippingPrice: cart.shippingPrice,
                taxPrice: cart.taxPrice,
                totalPrice: cart.totalPrice,
            },
            {
                headers: {
                    authorization: `Bearer ${userInfo.token}`
                }
            }
           )
           ctxDispatch({type: 'CART_CLEAR'});
           dispatch({type: 'CREATE_SUCCESS'});
           localStorage.removeItem('cartItems');
           console.log('before _id')
           navigate(`/order/${data.er._id}`);
           console.log('check _id')

        } catch (err) {
            dispatch({Type: 'CREATE_FAIL'});
            toast.error(getError(err));
        }
    }

    useEffect(()=>{
        if(!cart.paymentMethod){
            navigate('/payment')

        }
    },[cart])  

  return (
    <div>
        <CheckoutSteps step1 step2 step3 step4></CheckoutSteps>
        <Helmet>
            <title>Preview Order</title>
        </Helmet>
        <h1 className='my-3'>Preview Order</h1>
        <Row>
            <Col md={8}>
                <Card className='mb-3'>
                    <Card.Body>
                        <Card.Title>Shipping</Card.Title>
                        <Card.Text>
                            <strong>Name:</strong> {cart.shippingAddress.fullName} <br/>
                            <strong>Address: </strong> {cart.shippingAddress.address},
                            {cart.shippingAddress.city}, {cart.shippingAddress.zipCode}, {cart.shippingAddress.country}
                        </Card.Text>
                        <Link to='/shipping'>Edit</Link>
                    </Card.Body>
                </Card>
                <Card className='mb-3'>
                    <Card.Body>
                        <Card.Title>Payment</Card.Title>
                        <Card.Text>
                            <strong>Method:</strong> {cart.paymentMethod}
                        </Card.Text>
                        <Link to='/payment'>Edit</Link>
                    </Card.Body>
                </Card>
                <Card className='mb-3'>
                    <Card.Body>
                        <ListGroup variant='flush'>
                            {cart.cartItems.map((item) => (
                                <ListGroup.Item key={item.id}>
                                    <Row className='align-items-center'>
                                        <Col md={6}>
                                            <img 
                                            src={item.image}
                                            alt='qwer'
                                            className='img-fluid rounded img-thumbnail'>
                                            </img>{' '}
                                            <Link to={`/product/${item.slug}`}>{item.name}</Link>
                                        </Col>
                                        <Col md={3}>
                                            <span>{item.quantity}</span>
                                        </Col>
                                        <Col md={3}>${item.price}</Col>                                            
                                    </Row>
                                </ListGroup.Item>
                            ))}
                        </ListGroup>
                        <Link to='/cart'>Edit</Link>
                    </Card.Body>
                </Card>                
            </Col>
            <Col md={3}> {/* md=4 out of 12 for columns*/}
                <Card>
                    <Card.Body>
                        <Card.Title>Order Summary</Card.Title>
                        <ListGroup variant='flush'>
                            <ListGroup.Item>
                                <Row>
                                    <Col>Items</Col>
                                    <Col>${cart.itemsPrice.toFixed(2)}</Col>
                                </Row>
                            </ListGroup.Item>
                            <ListGroup.Item>
                                <Row>
                                    <Col>Shipping</Col>
                                    <Col>${cart.shippingPrice.toFixed(2)}</Col>
                                </Row>
                            </ListGroup.Item>
                            <ListGroup.Item>
                                <Row>
                                    <Col>Tax</Col>
                                    <Col>${cart.taxPrice.toFixed(2)}</Col>
                                </Row>
                            </ListGroup.Item>
                            <ListGroup.Item>
                                <Row>
                                    <Col>Order Total</Col>
                                    <Col>${cart.totalPrice.toFixed(2)}</Col>
                                </Row>
                            </ListGroup.Item>
                            <ListGroup.Item>
                                <div className='d-grid'>
                                    <Button type='button' onClick={placeOrderHandler}
                                    disabled={cart.cartItems.lentgh === 0}
                                    >Place Order
                                    </Button>                                    
                                </div>
                                {loading && <LoadingSign></LoadingSign> }
                            </ListGroup.Item>
                        </ListGroup>
                    </Card.Body>
                </Card>                
             </Col>
        </Row> 
    </div>
  )
}
